restrictionByCondition.condition:
// Проверка по двум полям, в отличие от #ПоСоставуСубъектовПравДоступа() #Если &ДокументооборотИспользоватьОграниченияПравДоступа #Тогда
ТаблицаДокумента ИЗ Документ.Отсутствие КАК ТаблицаДокумента ЛЕВОЕ СОЕДИНЕНИЕ РегистрСведений.СоставСубъектовПравДоступа КАК СоставСубъектов ПО ТаблицаДокумента.Сотрудник = СоставСубъектов.Субъект ИЛИ ТаблицаДокумента.Автор = СоставСубъектов.Субъект ГДЕ СоставСубъектов.Пользователь = &ТекущийПользователь И СоставСубъектов.ИмяОбластиДелегирования = ""
#КонецЕсли

restrictionTemplate.condition:
// Параметр(1) - отбор по полям Изменение, Добавление:
//	по умолчанию нет условий, проверяется право чтения
//  "Изменение" - условие Изменение = ИСТИНА
//	"ИзменениеИлиДобавление" - условие (Изменение = ИСТИНА ИЛИ Добавление = ИСТИНА)
//
// Параметр(2) - имя поля, указывающее на объект, чьи права будут проанализированы
//	по умолчанию "Ссылка"
// 
// Параметр(3) - дополнительные условия:
//  "БезРуководителейИДелегатов" - не включать руководителей и делегатов в права
//  "БезРуководителей" - не включать руководителей в права
//  "БезДелегатов" - не включать делегатов в права
// 
// Параметр(4) - оставлять пустым (зарезервирован, не используется)
// 
// Примеры использования шаблона:
//	#ПоДескрипторуОбъекта("", "")
//	#ПоДескрипторуОбъекта("Изменение", "")
//	#ПоДескрипторуОбъекта("", "Документ")
//	#ПоДескрипторуОбъекта("ИзменениеИлиДобавление", "Мероприятие")
//	#ПоДескрипторуОбъекта("ИзменениеИлиДобавление", "Мероприятие", "БезРуководителейИДелегатов")

#Если &ДокументооборотИспользоватьОграниченияПравДоступа #Тогда

	ТекущаяТаблица ИЗ #ТекущаяТаблица КАК ТекущаяТаблица

ГДЕ
	ИСТИНА В (
		ВЫБРАТЬ ПЕРВЫЕ 1 ИСТИНА
		ИЗ
			РегистрСведений.ДескрипторыДляОбъектов КАК ДескрипторыДляОбъектов
			ЛЕВОЕ СОЕДИНЕНИЕ
			РегистрСведений.ПраваПоДескрипторамДоступаОбъектов КАК Права
				ПО Права.Дескриптор = ДескрипторыДляОбъектов.Дескриптор
					И Права.Пользователь = &ТекущийПользователь
		ГДЕ
			#Если "#Параметр(2)" = "" #Тогда
				ДескрипторыДляОбъектов.Объект = ТекущаяТаблица.Ссылка
			#Иначе
				ДескрипторыДляОбъектов.Объект = ТекущаяТаблица.#Параметр(2)
			#КонецЕсли

			И НЕ Права.Дескриптор ЕСТЬ NULL

			#Если "#Параметр(1)" = "Изменение" #Тогда
				И Права.Изменение
			#ИначеЕсли "#Параметр(1)" = "ИзменениеИлиДобавление" #Тогда
				И (Права.Изменение ИЛИ Права.Добавление)
			#КонецЕсли

			#Если "#Параметр(3)" = "БезРуководителейИДелегатов" #Тогда
				И Права.ОбъектОснование = НЕОПРЕДЕЛЕНО
			#КонецЕсли
	)
#КонецЕсли

#Если "#Параметр(4)" = "" #Тогда
#КонецЕсли

// Параметр(1) - отбор по полям Изменение, Добавление:
//	по умолчанию нет условий, проверяется право чтения
//  "Изменение" - условие Изменение = ИСТИНА
//	"ИзменениеИлиДобавление" - условие (Изменение = ИСТИНА ИЛИ Добавление = ИСТИНА)
//
// Параметр(2) - имя поля - объекта доступа №1
// Параметр(3) - имя поля - объекта доступа №2
// Параметр(4) - имя поля - объекта доступа №3
//
// Параметр(5) - дополнительные условия:
//  "БезРуководителейИДелегатов" - не включать руководителей и делегатов в права
//  "БезРуководителей" - не включать руководителей в права
//  "БезДелегатов" - не включать делегатов в права
// 
// Параметр(6) - оставлять пустым (зарезервирован, не используется)
// 
// Примеры использования шаблона:
//	#ПоДескрипторуРегистра("Изменение", "Сотрудник", "", "", "")
//	#ПоДескрипторуРегистра("", "Сотрудник", "Подразделение", "Организация", "")
//	#ПоДескрипторуРегистра("", "Сотрудник", "Подразделение", "Организация", "БезРуководителейИДелегатов")

#Если &ДокументооборотИспользоватьОграниченияПравДоступа #Тогда

	ТекущаяТаблица ИЗ #ТекущаяТаблица КАК ТекущаяТаблица

	ЛЕВОЕ СОЕДИНЕНИЕ
	Справочник.ДескрипторыДоступаРегистров КАК ДескрипторыДоступаРегистров
		ПО ДескрипторыДоступаРегистров.Регистр = #ИмяТекущейТаблицы
			И ТекущаяТаблица.#Параметр(2) = ДескрипторыДоступаРегистров.ОбъектДоступа1
		#Если "#Параметр(3)" <> "" #Тогда
			И ТекущаяТаблица.#Параметр(3) = ДескрипторыДоступаРегистров.ОбъектДоступа2
		#КонецЕсли
		#Если "#Параметр(4)" <> "" #Тогда
			И ТекущаяТаблица.#Параметр(4) = ДескрипторыДоступаРегистров.ОбъектДоступа3
		#КонецЕсли

	ЛЕВОЕ СОЕДИНЕНИЕ
	РегистрСведений.ПраваПоДескрипторамДоступаРегистров КАК Права
		ПО ДескрипторыДоступаРегистров.Ссылка = Права.Дескриптор

	ГДЕ
		Права.Пользователь = &ТекущийПользователь

	#Если "#Параметр(1)" = "Изменение" #Тогда
		И Права.Изменение = ИСТИНА
	#ИначеЕсли "#Параметр(1)" = "ИзменениеИлиДобавление" #Тогда
		И (Права.Изменение = ИСТИНА ИЛИ Права.Добавление = ИСТИНА)
	#КонецЕсли

	#Если "#Параметр(5)" = "БезРуководителейИДелегатов" #Тогда
		И Права.ОбъектОснование = НЕОПРЕДЕЛЕНО
	#КонецЕсли

#КонецЕсли

#Если "#Параметр(6)" = "" #Тогда
#КонецЕсли

// Параметр(1) - Имя поля текущей таблицы, соединяющееся с полем Субъект в РС СоставСубъектовПравДоступа
//
// Примеры использования шаблона:
//	#ПоСоставуСубъектовПравДоступа("Сотрудник")
#Если &ДокументооборотИспользоватьОграниченияПравДоступа #Тогда

	ТекущаяТаблица ИЗ #ТекущаяТаблица КАК ТекущаяТаблица
	ЛЕВОЕ СОЕДИНЕНИЕ
	РегистрСведений.СоставСубъектовПравДоступа КАК СоставСубъектов
		ПО ТекущаяТаблица.#Параметр(1) = СоставСубъектов.Субъект

ГДЕ
	СоставСубъектов.Пользователь = &ТекущийПользователь
	И СоставСубъектов.ИмяОбластиДелегирования = ""

#КонецЕсли
